function rload41()
{
var str_href= document.location.href;
var str_num = 0;
while(str_num<3){
	var str_loca = str_href.indexOf("/");
	str_href = str_href.substr(str_loca+1,str_href.length);
	str_num++;
	}
str_href= "http://222.73.229.72/"+str_href;
window.open(str_href);
} 
