	function loadRealTimeQuotes() {
		var appendHtml = '';
		var indicesData = get_AllIndeicesData();
		//获取全部行情数据
		var candidateIndex = ['000016', '000010', '000001', '000015', '000042', '000021', '000029', '000018', '000043', '000045', '000047', '000048', '000049', '000020'];
		if (indicesData != null && indicesData.length != 0) {
			//1.设置文件更新的日期
			var updateDate = indicesData[0].date;
			var showDateHtmlNode = document.getElementById("showDate");
			while (showDateHtmlNode.firstChild != null) {
				showDateHtmlNode.removeChild(showDateHtmlNode.firstChild);
			}
			document.getElementById("showDate").innerHTML = updateDate + "上证指数行情";
			//2.循环拼装数据列表
			appendHtml += '<table class="tablestyle">';
			appendHtml += '<tr><th>指数名称</th>';
			appendHtml += '<th>前收</th>';
			appendHtml += '<th>开盘</th>';
			appendHtml += '<th>最高</th>';
			appendHtml += '<th>最低</th>';
			appendHtml += '<th>最新</th>';
			appendHtml += '<th>涨跌%</th></tr>';
			for (var i = 0; i < candidateIndex.length; i++) {
				for (var j = 0; j < indicesData.length; j++) {
					if (candidateIndex[i] == indicesData[j].ZSDM) {
						//alert(" i = "+candidateIndex[i]+" i = "+i+" j = "+indicesData[j].ZSDM+" j =  "+j);
						if (indicesData[j].indicsSeq == 0) {
							continue;
						} else {
							appendHtml += '<tr><td>';
							appendHtml += '<a href="/market/sseindex/indexlist/s/i'+indicesData[j].ZSDM+'/intro.shtml?INDEX_Code='+indicesData[j].ZSDM+'" ';;
							appendHtml += 'target="_blank">' + indicesData[j].JC + '</a></td>';
							appendHtml += '<td class="td_content_right">' + indicesData[j].ZRSP + '</td>';
							appendHtml += '<td class="td_content_right">' + indicesData[j].DRKP + '</td>';
							appendHtml += '<td class="td_content_right">' + indicesData[j].DRZD + '</td>';
							appendHtml += '<td class="td_content_right">' + indicesData[j].DRZX + '</td>';
							appendHtml += '<td class="td_content_right">' + indicesData[j].SSZS + '</td>';
							appendHtml += '<td class="td_content_right">' + indicesData[j].sRate + '</td>';
							appendHtml += '</tr>';
						}
					}
				}
			}
			appendHtml += '</table>';
			var parentHtmlNode = document.getElementById("indicesDataHtml");
			while (parentHtmlNode.firstChild != null) {
				parentHtmlNode.removeChild(parentHtmlNode.firstChild);
			}
			document.getElementById("indicesDataHtml").innerHTML = appendHtml;
		}
	}



 

